import {GET_CURRENT , SEARCH_CURRENT , GET_FORECAST , SEARCH_FORECAST} from '../Actions/index';

const initialState = {
    cityCurrent : {},
    cityForecast : {},
    searchCities: false,
    forecastCities
};

const Reducer = (state=initialState, action) => {
    switch (action.type) {

     case GET_CURRENT :
         return {
             ...state,
             city : action.payload,
         }
    case SEARCH_CURRENT :
         return {
             ...state,
             searchCities : state.searchCities + action.payload,
         }
    case GET_FORECAST :
            return {
                ...state,
                city : action.payload,
            }
    case SEARCH_FORECAST :
            return {
                ...state,
                searchCities : state.searchCities + action.payload,
            }     
    default :
    return state;
    }
}



export default Reducer;
