import axios from 'axios';
export const GET_CITY = 'GET_CITY';
export const SEARCH_CITY = 'SEARCH_CITY';
export const GET_FORECAST = 'GET_FORECAST';
export const SEARCH_FORECAST = SEA

export function getCurrent (city) {
    if (!city) { 
    return (dispatch) => {
      axios.get(`http://localhost:3001/v1/current`)
      .then((response) => { 
        dispatch({
            type: GET_CITY,
            payload: response.data
        })
       })   
    }
    }
    return (dispatch) => {
      axios.get(`http://localhost:3001/v1/forecast/${city}`)
      .then((response) => { 
        console.log(response.data)
        dispatch({
            type: SEARCH_CITY,
            payload: response.data
        })
       })   
    }
}

export function getForecast (city) {
  if (!city) { 
  return (dispatch) => {
    axios.get(`http://localhost:3001/v1/forecast`)
    .then((response) => { 
      dispatch({
          type: GET_FORECAST,
          payload: response.data
      })
     })   
  }
  }
  return (dispatch) => {
    axios.get(`http://localhost:3001/v1/forecast/${city}`)
    .then((response) => { 
      dispatch({
          type: SEARCH_FORECAST,
          payload: response.data
      })
     })   
  }
}
