import { GET_DETAIL , GET_COUNTRIES, POST_ACTIVITY , GET_ALL_COUNTRIES, GET_ALL_ACTIVITIES} from '../Actions/actions';

const initialState = {
    city : {},
    countriesDetail: [],
    allcountries:[],
    activities:[],

};

const Reducer = (state=initialState, action) => {
    switch (action.type) {

     case POST_ACTIVITY:
         return {
             ...state,
             activities: action.payload,
         }

    default :
    return state;
    }
}



export default Reducer;
