import axios from 'axios';
export const GET_CITY = 'GET_CITY';
export const SEARCH_CITY = 'SEARCH_CITY';

export function getCity (city) {
    if (!city) { 
    return (dispatch) => {
      axios.get(`http://localhost:3001/v1/location`)
      .then((response) => { 
        dispatch({
            type: GET_CITY,
            payload: response.data
        })
       })   
    }
    }
    return (dispatch) => {
      axios.get(`http://localhost:3001/v1/location?city=${city}`)
      .then((response) => { 
        dispatch({
            type: SEARCH_CITY,
            payload: response.data
        })
       })   
    }
}

export  function getCountries (page , pageSize , order) {
   return (dispatch) => {
      axios.get(`http://localhost:3001/countries/all/all?page=${page}&pageSize=${pageSize}&order=${order}`)
      .then((response) => { 
        dispatch({
            type: GET_CITY,
            payload: response.data
        })
       })
  };
}


export function search(input) {
  return function (dispatch) {
    axios.get(`http://localhost:3001/paises?name=${input}`).then((response) => {
      dispatch({
        type: SEARCH,
        payload: response.data,
      });
    });
  };
}
